"""
Approximate Pattern Matching Problem: Find all approximate occurrences of a pattern in a string.

Input: Strings Pattern and Text along with an integer d.
Output: All starting positions where Pattern appears as a substring of Text with at most d mismatches.
Code Challenge: Solve the Approximate Pattern Matching Problem.
"""

def HammingDistance(string1, string2):
    distance = 0
    for i in range(len(string1)):
        if string1[i] != string2[i]:
            distance += 1
    return distance

# def ApproximatePatternMatching(Pattern, Text, d):
#     positions = []
#     for i in range(len(Text) - len(Pattern) + 1):
#         if sum([1 for j in range(len(Pattern)) if Pattern[j] != Text[i + j]]) <= d:
#             positions.append(i)
#     return " ".join(map(str, positions)).strip()

def ApproximatePatternMatching(Pattern, Text, d):
    positions = []
    for i in range(len(Text) - len(Pattern) + 1):
        if HammingDistance(Pattern, Text[i:i+len(Pattern)]) <= d:
            positions.append(i)
    return " ".join(map(str, positions)).strip()

if __name__ == "__main__":
    Pattern = "ATTCTGGA"
    Text = "CGCCCGAATCCAGAACGCATTCCCATATTTCGGGACCACTGGCCTCCACGGTACGGACGTCAATCAAAT"
    d = 3
    print(ApproximatePatternMatching(Pattern, Text, d))

    for i in range(4):
        file_input = open(f"ApproximatePatternMatching/inputs/input_{i + 1}.txt")
        file_input_text = file_input.readlines()
        Pattern = file_input_text[0].strip()
        Text = file_input_text[1].strip()
        d = int(file_input_text[2].strip())

        file_output = open(f"ApproximatePatternMatching/outputs/output_{i + 1}.txt")
        file_output_text = file_output.read().strip()

        if file_output_text.strip() == ApproximatePatternMatching(Pattern, Text, d):
            print("yes")
        else:
            print(f"{i+1} no {ApproximatePatternMatching(Pattern, Text, d)}, {file_output_text}")
    
    Pattern = "ACTGATCCC"
